select * from customer limit 10
-- Total revenue generated by male and female customers
select gender, SUM(purchase_amount) as revenue
from customer
group by gender
-- customers used discount but purchased more than average purchase amount
select customer_id,purchase_amount
from customer
where discount_applied='Yes' and purchase_amount >= (select AVG(purchase_amount) from customer)
-- Top 5 products with the highest average review rating

select item_purchased,round(avg(review_rating::numeric),2) as "average product rating"
from customer
group by item_purchased
order by avg(review_rating) desc
limit 5
-- Compare the average purchase amount between 	Standard and Express Shipping
select shipping_type,
round(avg(purchase_amount),2) from customer
where shipping_type in ('Express','Standard')
group by shipping_type
--Do subscribed customers spend more? Comapare average spend and total revenue between subscribers and non subscribers
select subscription_status,
count (customer_id) as total_customers,
round(avg(purchase_amount),2) as average_spend,
round(sum(purchase_amount),2) as total_revenue from customer
group by subscription_status
order by total_revenue,average_spend desc;
-- which 5 products have the highest percentage of purchases with discount applied?
select item_purchased,
round(100*sum(case when discount_applied='Yes' then 1 else 0 end)/count(*),2)as discount_rate
from customer
group by item_purchased
order by discount_rate desc
limit 5;
-- Segment customers  into new, returning and loyal based on their total number of previous purchases and show the count of each segment
with customer_type as (
select customer_id, previous_purchases,
case when previous_purchases=1 then 'new'
when previous_purchases between 2 and 10 then 'returning'
else 'loyal' 
end as customer_segment
from customer

)
select customer_segment, count(*) as "Number of Customers"
from customer_type
group by customer_segment

-- Top 3 most purchased products within each category
with item_counts as (
select category, item_purchased,
COUNT(customer_id) as total_orders,
row_number () over (partition by category order by count(customer_id) desc) as item_rank
from customer
group by category,item_purchased
)
select item_rank,category,item_purchased,total_orders
from item_counts
where item_rank<=3;
--Are customers who are repeat buyers (more than 5 previous purchases) likely to subscribe?
select subscription_status,
count(customer_id) as repeat_buyers
from customer
where previous_purchases > 5
group by subscription_status
-- What is the revenue contribution of each age group?
SELECT
    age_group,
    SUM(purchase_amount) AS total_revenue
FROM customer
GROUP BY age_group;



